private final class XPathDelegate extends QItemDelegate {    
    @Override
    public QWidget createEditor(QWidget parent, QStyleOptionViewItem option, QModelIndex index) {
        final ValEditor<?> editor;
        switch (index.column()) {
            case 1:
                editor = new ValIntEditor(Environment, parent, new EditMaskInt(), true, false);
                break;
            case 2:
                editor = new ValStrEditor(Environment, parent, new EditMaskStr(), false, false);
                break;
            default:
                editor = null;
        }
        if (editor != null)
            editor.changeStateForGrid();
        return editor;
    }

    @Override
    public void setEditorData(QWidget editor, QModelIndex index) {
        final Object data = index.data(Qt.ItemDataRole.UserRole);
        if (index.column() == 1) {
            Long value = Long.valueOf(1);
            if (data instanceof Long) {
                value = (Long)data;
            }
            ((ValIntEditor)editor).setValue(value);
        } else if (index.column() == 2) {
            String value = null;
            if (data instanceof String) {
                value = (String)data;
            }
            ((ValStrEditor)editor).setValue(value);
        }
    }

    @Override
    public void setModelData(QWidget editor, QAbstractItemModel model, QModelIndex index) {
        final ValEditor<?> valEditor = ((ValEditor<?>)editor);
        final Object value = valEditor.getValue();
        model.setData(index, value, Qt.ItemDataRole.UserRole);
        model.setData(index, /*valEditor.getEditMask().toStr(

();
    }
}

