is
    vDayOfWeek int;
begin
    vDayOfWeek := TO_NUMBER(TO_CHAR(pDate, 'D'));
    --   =2, =3, ... =1,       
    vDayOfWeek := (vDayOfWeek + 5 - TO_NUMBER(TO_CHAR(TO_DATE('04-01-2007', 'DD-MM-YYYY'), 'D'))) mod 7;
    if vDayOfWeek = 0 THEN
        vDayOfWeek := 7;
    end if;
    return vDayOfWeek;
end;

