     newItemCountSize := LENGTH(newItemCount );
     oldItemCountSize := LENGTH(to_char(oldHeaderSize));
     if (oldItemCountSize <> newItemCountSize) then
           -- slow branch
           dbms_lob.createTemporary(tempClob, true); 
