is
   arr ARR_STR := ARR_STR();
   item VARCHAR2(32767);
   temp VARCHAR2(30);

   pos1   INTEGER;
   pos2   INTEGER;
   len    INTEGER;   
   ind_   INTEGER; 
   size_  INTEGER; 
begin
   if (lob is null) or (DBMS_LOB.GETLENGTH(lob) < 2) then
       return arr;
   end if;

   pos1 := INSTR(lob, '[');

   if (pos1<2) then
       return arr;
   end if;   

   pos2  := INSTR(lob, ']', pos1);

   temp  := SUBSTR(lob, 1, pos1-1);



   size_:= TO_NUMBER(temp);

   arr.Extend(size_);

   FOR ind_ IN 1 .. size_ 
    loop        
       len := -1;
       temp := SUBSTR(lob, pos1+1, pos2-pos1-1);
       if (temp is not null and LENGTH(temp) > 0) then
           len := TO_NUMBER(temp);
       else
           len := 0;
       end if;

       item := null;
       if (len > -1) then
           item := SUBSTR(lob, pos2+1, len);
       end if;       


       pos1 := pos2 + len + 1;
       pos2 := INSTR(lob, ']', pos1);

       arr(ind_) := item;       
   end loop;

   return arr;
end;

is
   res clob;
   size_ integer;
   temp VARCHAR2(30);
begin
 dbms_lob.createTemporary(res, true); 
 if (arr_ is null) then
   return res;
 end if;
 size_ := arr_.count();
 temp := to_char(size_);
 DBMS_LOB.writeappend(res, length(temp), temp);
 if size_<>0 then
    FOR element# IN arr_.FIRST() .. arr_.LAST() 
       LOOP 
          IF (arr_.EXISTS(element#))
          THEN 
             size_ := length(arr_(element#));             
             if ((size_ is null) or (size_ = 0)) then
                DBMS_LOB.writeappend(res, 3, '[0]');
             else
                temp := to_char(size_);
                DBMS_LOB.writeappend(res, 1, '[');
                DBMS_LOB.writeappend(res, length(temp), temp);
                DBMS_LOB.writeappend(res, 1, ']');
                DBMS_LOB.writeappend(res, size_, arr_(element#));         
             end if;

          END IF;
       END LOOP; 
end if;

return res;
end;

