                editor = null;
        }
        if (editor != null)
            editor.changeStateForGrid();
        return editor;
    }

    @Override
    public void setEditorData(QWidget editor, QModelIndex index) {
        final Object data = index.data(Qt.ItemDataRole.UserRole);
        if (index.column() == 1) {
            Long value = Long.valueOf(1);
            if (data instanceof Long) {
                value = (Long)data;
            }
            ((ValIntEditor)editor).setValue(value);
        } else if (index.column() == 2) {
            String value = null;
            if (data instanceof String) {
                value = (String)data;
            }
            ((ValStrEditor)editor).setValue(value);
        }
    }

